function corrector(pregunta, respuesta){
		
	var _pregunta = pregunta.trim();
	var _respuesta = respuesta.trim();
	
	var _respuestas=[
		'Cristobal Colón',
		'Lisboa',
	];
		
	var _correcciones = {
		regExPregunta0 : /^(Cristobal Col(o|ó)|Col(o|ó))n$/i,
		regExPregunta1 : /^Lisboa$/i
	};
	
	var _resultados = {
		correcto : 'correcto',
		incorrecto : 'incorrecto, la respuesta correcta es '+_respuestas[_pregunta]
	};
		
	return{
		corregir : function(){
		  switch(_pregunta) {
		    case '0':
				return (_correcciones.regExPregunta0.test(_respuesta)) ? _resultados.correcto : _resultados.incorrecto;
		    case '1':
				return (_correcciones.regExPregunta1.test(_respuesta)) ? _resultados.correcto : _resultados.incorrecto;
			//Caso no existe la pregunta
			default:
				return false;
			}
	  	} 
	};
}	

module.exports = corrector;
